import React, { useState, useEffect } from 'react'
import { Row, Col } from 'react-bootstrap'
import Loader from '../components/Loader'
//import Paginate from '../components/Paginate'
//import ProductCarousel from '../components/ProductCarousel'
import { news } from './news'
import NewsCard from './NewsCard'
import axios from 'axios'

const HomeScreen = () => {

  const loading = false;

  const [topheadlines, setTopHeadlines] = useState([]);
 
  const fetchTopHeadlines = async () => {
    try{
    const topNews = await axios.get('https://newsapi.org/v2/top-headlines?country=in&apiKey=cb405a88a7eb4e2f9096feab96c45403')
      console.log(topNews)  
    setTopHeadlines((topheadlines) => [
        ...topheadlines,
         topNews.data.articles
      ])
     
    }catch(err){
      console.log(err)
    }
  }
 
  useEffect(() => {
    fetchTopHeadlines()
  },[])


  return (
    <>
      <h1>Top Trending</h1>
      {loading ? (
        <Loader />
      ) : (
        <>
          <Row>
            {news.map((topNews) => (
              <Col key={topNews._id} sm={6} md={6} lg={6} xl={4}>
                <NewsCard topNews={topNews} />
              </Col>
            ))}
          </Row>
          {/* <Paginate
            pages={pages}
            page={page}
            keyword={keyword ? keyword : ''}
          /> */}
        </>
      )}
    </>
  )
}

export default HomeScreen
