import React from 'react'
import { Carousel} from 'react-bootstrap'
import { data } from './data'

const NewsCarousel = () => {
  return (
    <Carousel fade>
         {data.articles.map((topnews) => (
                <Carousel.Item>
                <img
                style={{maxWidth: '800px', height:'400px', textAlign: 'center'}}
                  className="d-block w-100"
                  src={topnews.urlToImage}
                />
                <Carousel.Caption>
                  <h3>{topnews.title.slice(0,50)}</h3>
                </Carousel.Caption>
              </Carousel.Item>
            ))}
    </Carousel>
  )
}

export default NewsCarousel
